mapper.select:
SELECT 
  TRUNC(e.emp_regdate) AS emp_regdate
FROM employees e
WHERE e.emp_idx = #{empIdx}
  AND e.co_idx  = #{coIdx}

SELECT DISTINCT e.emp_name, ar.attd_status, d.dept_name, e.emp_grade FROM employees e LEFT JOIN attendance_records ar ON e.emp_idx = ar.emp_idx LEFT JOIN departments d ON e.dept_idx = d.dept_idx
AND e.emp_name LIKE '%' || #{empName} || '%'
AND ar.attd_status = #{attdStatus}
AND d.dept_name LIKE '%' || #{deptName} || '%'
AND e.emp_grade = #{empGrade}
