mapper.select:
SELECT DISTINCT e.emp_idx, e.emp_name, e.dept_idx, d.dept_name, e.emp_grade, e.emp_status, e.emp_type, e.emp_email, e.emp_regdate, e.emp_account, e.emp_memo, e.emp_addr, e.emp_addr_detail, e.emp_addr_zip FROM employees e LEFT JOIN departments d ON e.dept_idx = d.dept_idx
AND (d.dept_up_idx = #{deptUpIdx} OR d.dept_idx = #{deptUpIdx})
AND e.dept_idx = #{deptIdx}
AND (e.emp_grade = #{empGrade} OR e.emp_grade LIKE '%' || #{empGrade} || '%')
AND e.emp_status = #{empStatusType}
AND e.emp_idx LIKE '%' || #{keyword} || '%'
AND e.emp_name LIKE '%' || #{keyword} || '%'
AND ( e.emp_idx LIKE '%' || #{keyword} || '%' OR e.emp_name LIKE '%' || #{keyword} || '%' )
AND e.co_idx = #{coIdx}
ORDER BY e.emp_idx ASC

SELECT emp_regdate
FROM employees
WHERE emp_idx = #{empIdx}

SELECT e.emp_idx, e.emp_name, e.emp_status, e.emp_type, e.emp_regdate, e.emp_phone, e.emp_email, e.emp_contract, d.dept_name, g.grd_name FROM employees e LEFT JOIN departments d ON e.dept_idx = d.dept_idx LEFT JOIN grade g ON e.grd_idx = g.grd_idx
AND (d.dept_up_idx = #{deptUpIdx} OR d.dept_idx = #{deptUpIdx})
AND e.dept_idx = #{deptIdx}
AND (e.emp_grade = #{empGrade} OR e.emp_grade LIKE '%' || #{empGrade} || '%')
AND e.emp_status = #{empStatus}
AND e.emp_idx LIKE '%' || #{keyword} || '%'
AND e.emp_name LIKE '%' || #{keyword} || '%'
AND ( e.emp_idx LIKE '%' || #{keyword} || '%' OR e.emp_name LIKE '%' || #{keyword} || '%' )
AND e.co_idx = #{coIdx}

SELECT COUNT(*) FROM employees e LEFT JOIN departments d ON e.dept_idx = d.dept_idx LEFT JOIN grade g ON e.grd_idx = g.grd_idx
AND (d.dept_up_idx = #{deptUpIdx} OR d.dept_idx = #{deptUpIdx})
AND e.dept_idx = #{deptIdx}
AND (e.emp_grade = #{empGrade} OR e.emp_grade LIKE '%' || #{empGrade} || '%')
AND e.emp_status = #{empStatus}
AND e.emp_idx LIKE '%' || #{keyword} || '%'
AND e.emp_name LIKE '%' || #{keyword} || '%'
AND ( e.emp_idx LIKE '%' || #{keyword} || '%' OR e.emp_name LIKE '%' || #{keyword} || '%' )
AND e.co_idx = #{coIdx}

mapper.update:
MERGE INTO employee e
USING (
    SELECT #{empIdx}, #{empName}, #{rrnPfx}, #{rrnSfx}, #{empEmail}, #{deptIdx}, #{empGrade}, #{empType}, #{empRegdate},
        #{empAccount}, #{empPhone}, #{empPw}, #{empAddr}, #{empAddrDetail}, #{empAddrZip}, #{coIdx}, #{empImg}
    FROM dual
) src
