mapper.select:
SELECT COUNT(*) AS total, SUM(CASE WHEN lr.approval_status = 'AS01' THEN 1 ELSE 0 END) AS pending, SUM(CASE WHEN lr.approval_status = 'AS02' THEN 1 ELSE 0 END) AS approved, SUM(CASE WHEN lr.approval_status = 'AS03' THEN 1 ELSE 0 END) AS rejected FROM leave_requests lr LEFT JOIN employees e ON lr.emp_idx = e.emp_idx
1=1
lr.approval_status = #{approvalStatus}
lr.req_type = #{reqType}
AND lr.request_date BETWEEN TO_DATE(#{reqStart}, 'YYYY-MM-DD') AND TO_DATE(#{reqEnd}, 'YYYY-MM-DD')
AND lr.request_date BETWEEN TO_DATE(#{reqStart}, 'YYYY-MM-DD') AND TRUNC(SYSDATE)
AND e.co_idx = #{coIdx}

SELECT COUNT(*)
FROM leave_requests lr
    LEFT JOIN employees e ON lr.emp_idx = e.emp_idx
    LEFT JOIN departments d ON e.dept_idx = d.dept_idx


        lr.approval_status = #{approvalStatus}


        lr.req_type = #{reqType}



            AND lr.request_date BETWEEN TO_DATE(#{reqStart}, 'YYYY-MM-DD') AND TO_DATE(#{reqEnd}, 'YYYY-MM-DD')


            AND lr.request_date BETWEEN TO_DATE(#{reqStart}, 'YYYY-MM-DD') AND TRUNC(SYSDATE)



    AND e.co_idx = #{coIdx}

SELECT lr.leave_req_idx, lr.emp_idx, lr.approver_idx, lr.leave_Start_date, lr.leave_end_date, lr.reason, lr.request_date, lr.approval_status, lr.approved_date, lr.att_file, lr.co_idx, lr.req_type, e.emp_img, e.emp_name, d.dept_name FROM leave_requests lr LEFT JOIN employees e ON lr.emp_idx = e.emp_idx LEFT JOIN departments d ON e.dept_idx = d.dept_idx
lr.approval_status = #{approvalStatus}
lr.req_type = #{reqType}
AND lr.request_date BETWEEN TO_DATE(#{reqStart}, 'YYYY-MM-DD') AND TO_DATE(#{reqEnd}, 'YYYY-MM-DD')
AND lr.request_date BETWEEN TO_DATE(#{reqStart}, 'YYYY-MM-DD') AND TRUNC(SYSDATE)
AND e.co_idx = #{coIdx}
